CREATE TABLE IF NOT EXISTS `#__vbizz_stock` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(250) NOT NULL,
  `issue` int(11) NOT NULL,
  `item` int(11) NOT NULL,
  `quantity` int(11) NOT NULL,
  `description` text NOT NULL,
  `created_by` int(11) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=5 DEFAULT CHARSET=latin1;

CREATE TABLE IF NOT EXISTS `#__vbizz_stock_items` (
  `id` int(100) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL,
  `quantity` int(100) NOT NULL,
  `update_date` datetime NOT NULL,
  `user_id` int(100) NOT NULL,
  `description` text NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

CREATE TABLE IF NOT EXISTS `#__vbizz_support` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `ownerid` int(100) NOT NULL,
  `message` text NOT NULL,
  `attachment` varchar(250) NOT NULL,
  `category` int(11) NOT NULL,
  `topic` int(11) NOT NULL,
  `created_by` int(11) NOT NULL,
  `created` datetime NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;


CREATE TABLE IF NOT EXISTS `#__vbizz_support_category` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `ownerid` int(100) NOT NULL,
  `title` varchar(250) NOT NULL,
  `description` text NOT NULL,
  `created_by` int(11) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=8 DEFAULT CHARSET=utf8;


CREATE TABLE IF NOT EXISTS `#__vbizz_support_topic` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `ownerid` int(100) NOT NULL,
  `subject` text NOT NULL,
  `alias` text,
  `category` int(11) NOT NULL,
  `commentid` int(11) NOT NULL,
  `created_by` int(11) NOT NULL,
  `created` datetime NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;

CREATE TABLE IF NOT EXISTS `#__vbizz_tax` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `ownerid` int(100) NOT NULL,
  `tax_name` varchar(250) NOT NULL,
  `tax_value` int(11) NOT NULL,
  `published` int(2) NOT NULL DEFAULT '1',
  `tax_desc` text NOT NULL,
  `created_by` int(11) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;


CREATE TABLE IF NOT EXISTS `#__vbizz_templates` (
